import React from 'react';
import './card.styles.css';
import {Like} from '../../components/likes/like.component';
import {Unlike} from '../../components/likes/unlike.component';


export const Card = (props) => {
    let content="",heading="";
    for(let i=0;i<props.monster[3].length;i++){
        if(props.monster[3][i]==='<'){
            i=i+37;
            content=content + "engine ";
        }
        else {
            content=content + props.monster[3][i];
        }
    }
    for(let i=0;i<props.monster[2].length;i++){
        if(props.monster[2][i]==='<'){
            i=i+37;
            heading=heading + "engine ";
        }
        else {
            heading=heading + props.monster[2][i];
        }
    }
    var remaining=0;
    // if(heading.length<72)remaining=72-heading.length;
    if(heading.length<=35)heading = Array(4).fill('\xa0').join('\n') + heading;
    else if(heading.length<72)heading = heading + Array(2).fill('\xa0').join('\n') + ' ';
    if(heading[0])
    // heading = heading + Array(remaining).fill('\xa0').join('') + ' ';
    return (
    <div className="card">
        <div className="card-header">
            <p className="user-name">{heading}</p>
            <p className="time">{props.monster[1]}</p>
        </div>
        <div className="card-image">
            <img alt="monster" src={props.monster[10]} width="100%" />
        </div>
        <div className="card-actions">
            {
                props.isLiked[props.monster[13]] ? <Unlike setChange={props.setChange} index={props.monster[13]} /> : <Like setChange={props.setChange} index={props.monster[13]} />
            }
	  </div>
        <div className="card-content">
            <a href="#" class="hashtag">#{props.monster[5]}</a>
            <p>{content}</p>
        </div>
    </div>
    );
}
