import React, { Component } from 'react';
import './App.css';
import { CardList } from './components/card-list/card-list.component';
import { SearchBox } from './components/search-box/search-box.component';


class App extends Component{
  constructor(){
    super();

    this.state={
      monsters: [],
      searchfield: ''
    };
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(response => response.json())
    .then(users => this.setState({monsters: users}));
  }

  render() {
    const {monsters,searchfield }=this.state;
    const filteredMonsters=monsters.filter(monster =>
      monster.name.toLowerCase().includes(searchfield.toLowerCase())
      );


    return (
      <div className="App">
        <header>
          <h1> <i class="fa fa-instagram" aria-hidden="true"></i> unicornstagram</h1>
        </header>
        <section class="profile">
          <img class="avatar" src="http://www.cornel1801.com/1/l/LAST-UNICORN-1982/The-Last-Unicorn/videosong1.jpg" alt="my profile picture" />
          <div class="profile-info">
            <h1>Alexander</h1>
            <p>Hi. I like unicorns. I am a unicorn, and I also take lots of pictures of unicorns. They are the best. Better than anything else in the whole wide world.</p>
          </div>
        </section>
        {/* <h1>Monsters Rolodex</h1> */}
        <SearchBox 
          placeholder='seacrh robots'
          handleChange={e =>this.setState({searchfield: e.target.value})}
        />
        <CardList monsters={filteredMonsters} />
      </div>
    );
  }

}


export default App;
