import React, { Component } from 'react';
import './App.css';
import { CardList } from './components/card-list/card-list.component';
import { SearchBox } from './components/search-box/search-box.component';
import {WithSpinner} from './components/'

class App extends Component{
  constructor(){
    super();

    this.state={
      monsters: [],
      searchfield: '',
      isLiked: []
    };
  }

  // componentDidMount(){
  //   fetch('https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&api_key=yGTtYuwPkMhpG6ZxtAqdsmndh2lVNgJsMboKOUFi')
  //   .then(response => response.json())
  //   .then(users => { this.setState({monsters: users.photos})});
  // }
  componentDidMount(){
    fetch('https://api.nasa.gov/techtransfer/patent/?engine&api_key=yGTtYuwPkMhpG6ZxtAqdsmndh2lVNgJsMboKOUFi')
    .then(response => response.json())
    .then(users => this.setState({monsters: users.results}, function (){
      for(let i=0;i<this.state.monsters.length;i++){
        this.state.monsters[i].push(i);
      }
      for(let i=0;i<this.state.monsters.length;i++){
        this.state.isLiked.push(false);
      }
      // console.log(this.state.isLiked);
    }));
  }
  setChange = (x) => {
    // e.preventDefault();
    // console.log(this.state.isLiked[x]);
    this.state.isLiked[x]=!this.state.isLiked[x];
    this.setState({isLiked: this.state.isLiked}, function (){
      console.log(this.state.isLiked[x]);
    });
    // console.log(this.state.isLiked[x]);
  }

  render() {
    const {monsters,searchfield,isLiked}=this.state;
    // let filteredMonsters=[];
    // for(let i=0;i<100;i++){
    //   filteredMonsters.push(monsters[i]);
    // }
    // const filteredMonsters = monsters.filter(monster => (monster.id<=425200 && monster.id>=424906));
    // console.log(monsters);
    const filteredMonsters=monsters.filter(monster =>
      (monster[1].toLowerCase().includes(searchfield.toLowerCase()) || monster[2].toLowerCase().includes(searchfield.toLowerCase())
        || monster[3].toLowerCase().includes(searchfield.toLowerCase()) || monster[5].toLowerCase().includes(searchfield.toLowerCase())) 
    );
    // console.log(monsters);
    return (
      <div className="App">
        <div className="mainbody">
        <header>
          <h1> <i class="fa fa-instagram" aria-hidden="true"></i>Spacestagram</h1>
        </header>
        <section class="profile">
          <img class="avatar" src="http://www.cornel1801.com/1/l/LAST-UNICORN-1982/The-Last-Unicorn/videosong1.jpg" alt="" />
          <div class="profile-info">
            <h1>Neptune</h1>
            <p>Hi. I like Neptune. I am a planet, and I also take lots of pictures of planets. They are the best.</p>
          </div>
        </section>
        <SearchBox 
          placeholder='Filter patents by topic/content/hashtag/sensors'
          handleChange={e =>this.setState({searchfield: e.target.value})}
        />
        </div>
        <CardList monsters={filteredMonsters} isLiked={isLiked} setChange={this.setChange} />
      </div>
    );
    // return ( 
    //   <div className="App"> Testing</div>
    // );
  }

}


export default App;
